-- 导出 online_learning 的数据库结构
CREATE DATABASE IF NOT EXISTS `online_learning` /*!40100 DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci */ /*!80016 DEFAULT ENCRYPTION='N' */;
USE `online_learning`;

-- 导出  表 online_learning.course 结构
CREATE TABLE IF NOT EXISTS `course` (
  `course_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '课程id',
  `teacher_id` int(11) NOT NULL COMMENT '所属教师的id',
  `course_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程名称',
  `teacher_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '教师名称',
  `course_type` enum('计算机','外语','理学','工学','文史哲','其他') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程类型',
  `course_img` tinytext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程封面',
  `student_num` int(11) NOT NULL COMMENT '已报名学生数',
  `recommend` enum('0','1') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '是否上首页',
  `school_SN` char(50) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `creation_time` date NOT NULL COMMENT '当前课程创建时间',
  PRIMARY KEY (`course_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1456178162 DEFAULT CHARSET=utf8 COMMENT='课程表';

-- 正在导出表  online_learning.course 的数据：~190 rows (大约)
/*!40000 ALTER TABLE `course` DISABLE KEYS */;
/*!40000 ALTER TABLE `course` ENABLE KEYS */;

-- 导出  表 online_learning.course_discuss 结构
CREATE TABLE IF NOT EXISTS `course_discuss` (
  `discuss_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论id',
  `course_id` int(11) NOT NULL COMMENT '课程id',
  `user_id` int(11) NOT NULL COMMENT '发表用户id',
  `title` char(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `content` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `time` datetime NOT NULL COMMENT '发布时间',
  `is_teacher` enum('0','1') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '老师是否参与',
  PRIMARY KEY (`discuss_id`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8 COMMENT='课程讨论表';

-- 正在导出表  online_learning.course_discuss 的数据：~23 rows (大约)
/*!40000 ALTER TABLE `course_discuss` DISABLE KEYS */;
/*!40000 ALTER TABLE `course_discuss` ENABLE KEYS */;

-- 导出  表 online_learning.course_info 结构
CREATE TABLE IF NOT EXISTS `course_info` (
  `course_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '课程id',
  `brief_introduction` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '简介',
  `summary` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '课程概述',
  `target` mediumtext COMMENT '授课目标',
  `start_time` date DEFAULT NULL COMMENT '课程开始时间',
  `end_time` date DEFAULT NULL COMMENT '课程结束时间',
  `requirements` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '课程要求',
  `preparatory_knowledge` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '预备知识',
  `outline` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '课程大纲',
  `reference_material` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '参考资料',
  `common_problem` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '常见问题',
  PRIMARY KEY (`course_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1456178162 DEFAULT CHARSET=utf8;

-- 正在导出表  online_learning.course_info 的数据：~117 rows (大约)
